import jwt from 'jsonwebtoken'
import{useNavigate} from 'react-router-dom'
import  { useState } from "react";
import * as Realm from 'realm-web'
import { ApolloProvider, ApolloClient, HttpLink, InMemoryCache } from '@apollo/client'
import gql from 'graphql-tag'


const AppId = 'scanup-gwhxh'
const graphqlUri = `https://realm.mongodb.com/api/client/v2.0/app/${AppId}/graphql`
const App = new Realm.App({id: AppId})

function Connect() {
    const apolloClient = new ApolloClient({
        link: new HttpLink({
          uri: graphqlUri
        }),
        fetch: async (uri, options) => {
            const accessToken = App.currentUser;
            options.headers.Authorization = `Bearer ${accessToken}`;
            console.log('Client init');
            return fetch(uri, options);
          },
        cache: new InMemoryCache(),
      });
}




function  Login({setUser}){
    const loginAnonymous = async () => {
        const user = await App.logIn(Realm.Credentials.emailPassword("public@scanup.vicat.fr","LeAheBaWtTRbM3KP"));
        setUser(user);
        console.log(user);
    }
    return ( 
    <div>
        <button onClick={loginAnonymous}>Login</button>
        <button onClick={apolloClient}>Client</button>
        <button onClick={dataQuery}>Identifiants</button>

    </div> )

}
function dataQuery() {
const  {loading, error , data} = apolloClient(gql `
query allDivisions {
    ScanDivision {
        _id,
        certificat
    }

}`);
console.log(data);

}

const Dashboard = ()=> {
    const  history = useNavigate()
    const [quote, setQuote] = useState('')
    const [tempQuote, setTempQuote] = useState('')

    async function populateQuote(){
       const req =  await fetch('http://localhost:1337/api/quote',{headers :{
            'x-access-token': localStorage.getItem('token'),
        },
    })
    const data = await req.json()
if(data.status === 'ok'){
    setQuote(data.quote)
    }else{alert(data.error)}  
  }
  useEffect(() => {
    const token = localStorage.getItem('token')
    if (token) {
        const user = jwt.decode(token)
        if (!user) {
            localStorage.removeItem('token')
            history.replace('/login')
        } else {
            populateQuote()
        }
    }
},)
const [user,setUser] = React.useState(App.curentUser);

async function updateQuote(event) {
    event.preventDefault()

    const req = await fetch('http://localhost:1337/api/quote', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'x-access-token': localStorage.getItem('token'),
        },
        body: JSON.stringify({
            quote: tempQuote,
        }),
    })

    const data = await req.json() 
    if (data.status === 'ok') {
        setQuote(tempQuote)
        setTempQuote('')
    } else {
        alert(data.error)
    }
}

return (
    <ApolloProvider client={apolloClient}>
    <div>
        <h1>Your quote: {quote || 'No quote found'}</h1>
        <form onSubmit={updateQuote}>
            <input
                type="text"
                placeholder="Quote"
                value={tempQuote}
                onChange={(e) => setTempQuote(e.target.value)}
            />
            <input type="submit" value="Update quote" />
        </form>
       <Login  setUser={setUser}/>
		</div>
        </ApolloProvider>
	)
}

export default Dashboard
