import React, { useEffect } from "react";
import jwt from "jsonwebtoken";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import * as Realm from "realm-web";
import "./Dashboard.css";

const APPID = "scanup-bnmev";
const APP = new Realm.App({ id: APPID });

let tableau = [];

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = { list: [] };
  }

  loginAnonymous = async () => {
    const user = await APP.logIn(
      Realm.Credentials.emailPassword(
        "public@scanup.vicat.fr",
        "LeAheBaWtTRbM3KP"
      )
    );
    this.props.setUser(user);
    const client = APP.currentUser.mongoClient("mongodb-atlas");
    const db = client.db("scanup-db");
    const collection = db.collection("ScanDivision");
    const users = (await collection.find()).slice(0, 10);
    const newList = [];
    if (users) {
      console.log(users);
      for (let user of users) {
        newList.push(
          <h2>User : {user.user}</h2>,
          <li key={user.division}>division: {user.division}</li>,
          <li key={user.role}>Role:{user.role}</li>,
          <li key={user.certificat}>Certificat :{user.certificat}</li>,
          <button className="btn btn-update" value={user.user}>
            Update
          </button>,
          <button className="btn btn-delete" value={user.user}>
            Delete
          </button>,
          <div className="trait"></div>
        );
      }
      //newList.push(JSON.stringify(col));
      //alert(jsonDatas)
      this.setState({ list: newList });
    }
  };
  render() {
    return (
      <div>
        <button className="btn" onClick={this.loginAnonymous}>
          Users's list
        </button>
        <p>{this.state.list}</p>
        <ul></ul>
      </div>
    );
  }
}

const Dashboard = () => {
  const history = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const user = jwt.decode(token);
      if (!user) {
        localStorage.removeItem("token");
        history.replace("/login");
      } else {
      }
    }
  });
  const [user, setUser] = React.useState(APP.curentUser);

  return (
    <div className="main">
      <h1>Scan'up Dashboard </h1>
      <div className="main-buttons">
        <Login setUser={setUser} />
        <button className="btn ">Add user</button>
      </div>
    </div>
  );
};

export default Dashboard;
